  return (
    <div className="simon">
      <button onClick={iniciarJuego}>Comenzar Juego</button>
      {coloresDisponibles.map((color) => (
        <button
          key={color}
          className={`boton ${color}`}
          onClick={() => manejarClicColor(color)}
        ></button>
      ))}
      <div>
